%% Exercise 2.1 - Ripley datasetclear;close all;%% load datasetload ripley.mat%% Visualize the dataset% Trainfigure;hold onplot(Xtrain(1:125,1),Xtrain(1:125,2),'r+');plot(Xtrain(126:250,1),Xtrain(126:250,2),'bo');xlabel('x_1') ylabel('x_2') hold off% Testfigure;hold onplot(Xtest(1:500,1),Xtest(1:500,2),'r+');plot(Xtest(501:1000,1),Xtest(501:1000,2),'bo');xlabel('x_1') ylabel('x_2') %% General conftype='c'; % classificationalgorithm='simplex'; % or 'gridsearch'k = 10; % k-fold crossvalidation%% Linear kernelkernel_type='lin_kernel';model={Xtrain,Ytrain,type,[],[],kernel_type};[gam,cost] = tunelssvm(model,algorithm,'crossvalidatelssvm',{k,'misclass'});% Train the classification model[alpha,b] = trainlssvm({Xtrain,Ytrain,type,gam,[],kernel_type});% Classification on test data[Yest, Ylatent]=simlssvm({Xtrain,Ytrain,type,gam,[],kernel_type},{alpha,b},Xtest);% ROC curveroc(Ylatent,Ytest);%% Polynomial kernelkernel_type='poly_kernel';model={Xtrain,Ytrain,type,[],[],kernel_type};[gam,sig2,cost] = tunelssvm(model,algorithm,'crossvalidatelssvm',{k,'misclass'});% Train the classification model[alpha,b] = trainlssvm({Xtrain,Ytrain,type,gam,sig2,kernel_type});% Classification on test data[Yest, Ylatent]=simlssvm({Xtrain,Ytrain,type,gam,sig2,kernel_type},{alpha,b},Xtest);% ROC curveroc(Ylatent,Ytest);%% RBF kernelkernel_type='RBF_kernel';model={Xtrain,Ytrain,type,[],[],kernel_type};[gam,sig2,cost] = tunelssvm(model,algorithm,'crossvalidatelssvm',{k,'misclass'});% Train the classification model[alpha,b] = trainlssvm({Xtrain,Ytrain,type,gam,sig2,kernel_type});% Classification on test data[Yest, Ylatent]=simlssvm({Xtrain,Ytrain,type,gam,sig2,kernel_type},{alpha,b},Xtest);% ROC curveroc(Ylatent,Ytest);